	static class Solution {
	    public int uniquePathsWithObstacles(int[][] obstacleGrid) {
	        if(obstacleGrid[0][0] == 1){
	            return 0;
	        }
	        return visit(obstacleGrid, 0,0);
	    }
	    
	    int visit(int[][] obstacleGrid, int x , int y){
	        if(obstacleGrid.length==y+1 && obstacleGrid[y].length== x+1 && obstacleGrid[y][x]==0){
	            return 1;
	        }
	        int retu = 0;
	        if(x < obstacleGrid[y].length-1 && obstacleGrid[y][x+1] ==0){
	            retu = retu + visit(obstacleGrid, x+1,y);
	        }
	        if(y < obstacleGrid.length -1 && obstacleGrid[y+1][x]==0){
	            retu = retu + visit(obstacleGrid,x,y+1);
	        }
	        
	        return retu;
	    }
	}
